const items = $input.all();
const envVars = $('í™˜ê²½ë³€ìˆ˜ ì„¤ì •').first().json;
const tokenData = $('í‚¤ì›€ í† í° ë°œê¸‰').first().json;

const results = [];

for (const item of items) {
  const strategy = item.json;
  let stockCodes = [];

  if (strategy.filtered_stocks) {
    if (Array.isArray(strategy.filtered_stocks)) {
      stockCodes = strategy.filtered_stocks.filter(code => code && typeof code === 'string');
    } else if (typeof strategy.filtered_stocks === 'object') {
      stockCodes = Object.values(strategy.filtered_stocks).filter(code => code && typeof code === 'string');
    }
  }

  console.log(`ğŸ“Š Strategy: ${strategy.strategy_name}, Stock count: ${stockCodes.length}`);

  for (let i = 0; i < stockCodes.length; i++) {
    const stockCode = stockCodes[i];

    // ì²« ë²ˆì§¸ ì¢…ëª©ì´ ì•„ë‹ˆë©´ 60ì´ˆ ëŒ€ê¸°
    if (results.length > 0) {
      console.log(`â±ï¸ 60ì´ˆ ëŒ€ê¸° ì¤‘... (${results.length + 1}/${stockCodes.length})`);
      await new Promise(resolve => setTimeout(resolve, 60000));
    }

    console.log(`ğŸ” [${i + 1}/${stockCodes.length}] ${stockCode} ì¡°íšŒ ì¤‘...`);

    try {
      // í‚¤ì›€ í˜¸ê°€ ì¡°íšŒ
      const response = await fetch('https://mockapi.kiwoom.com/api/dostk/mrkcond', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json;charset=UTF-8',
          'authorization': `Bearer ${tokenData.token}`,
          'cont-yn': 'N',
          'next-key': '',
          'api-id': 'ka10004',
          'accept': 'application/json'
        },
        body: JSON.stringify({ stk_cd: stockCode })
      });

      const kiwoomData = await response.json();

      // ê°€ê²© íŒŒì‹±
      const parsePrice = (price) => {
        if (!price) return 0;
        return parseFloat(String(price).replace(/[+\-]/g, ''));
      };

      const selPrice = parsePrice(kiwoomData.sel_fpr_bid);
      const buyPrice = parsePrice(kiwoomData.buy_fpr_bid);
      const estimatedPrice = (selPrice + buyPrice) / 2;

      // ì¢…ëª©ëª… ì¡°íšŒ
      let stockName = stockCode;
      try {
        const metaResponse = await fetch(
          `${envVars.SUPABASE_URL}/rest/v1/stock_metadata?stock_code=eq.${stockCode}&select=stock_name`,
          {
            headers: {
              'apikey': envVars.SUPABASE_ANON_KEY,
              'Authorization': `Bearer ${envVars.SUPABASE_ANON_KEY}`
            }
          }
        );
        const metaData = await metaResponse.json();
        if (metaData && metaData.length > 0 && metaData[0].stock_name) {
          stockName = metaData[0].stock_name;
        }
      } catch (error) {
        console.error('Failed to fetch stock name:', error);
      }

      // ì „ì¼ ì¢…ê°€ ì¡°íšŒ
      let previousClose = estimatedPrice;
      try {
        const priceResponse = await fetch(
          `${envVars.SUPABASE_URL}/rest/v1/kw_price_daily?stock_code=eq.${stockCode}&select=close&order=trade_date.desc&limit=1`,
          {
            headers: {
              'apikey': envVars.SUPABASE_ANON_KEY,
              'Authorization': `Bearer ${envVars.SUPABASE_ANON_KEY}`
            }
          }
        );
        const priceData = await priceResponse.json();
        if (priceData && priceData.length > 0) {
          previousClose = parseFloat(priceData[0].close);
        }
      } catch (error) {
        console.error('Failed to fetch previous close:', error);
      }

      const changePrice = estimatedPrice - previousClose;
      const changeRate = previousClose > 0 ? ((changePrice / previousClose) * 100) : 0;

      const selVolume = parseInt(String(kiwoomData.sel_fpr_req || 0).replace(/[+\-]/g, '')) || 0;
      const buyVolume = parseInt(String(kiwoomData.buy_fpr_req || 0).replace(/[+\-]/g, '')) || 0;

      // ì¢…ëª© ë§ˆìŠ¤í„° ì €ì¥
      try {
        await fetch(`${envVars.SUPABASE_URL}/rest/v1/kw_stock_master`, {
          method: 'POST',
          headers: {
            'apikey': envVars.SUPABASE_ANON_KEY,
            'Authorization': `Bearer ${envVars.SUPABASE_ANON_KEY}`,
            'Content-Type': 'application/json',
            'Prefer': 'resolution=merge-duplicates'
          },
          body: JSON.stringify({
            stock_code: stockCode,
            stock_name: stockName,
            market: 'KOSPI'
          })
        });
      } catch (error) {
        console.error('Failed to save stock master:', error);
      }

      // í˜„ì¬ê°€ ì €ì¥
      try {
        await fetch(`${envVars.SUPABASE_URL}/rest/v1/kw_price_current`, {
          method: 'POST',
          headers: {
            'apikey': envVars.SUPABASE_ANON_KEY,
            'Authorization': `Bearer ${envVars.SUPABASE_ANON_KEY}`,
            'Content-Type': 'application/json',
            'Prefer': 'resolution=merge-duplicates'
          },
          body: JSON.stringify({
            stock_code: stockCode,
            stock_name: stockName,
            current_price: estimatedPrice,
            change_price: changePrice,
            change_rate: changeRate,
            volume: selVolume + buyVolume,
            high_52w: selPrice,
            low_52w: buyPrice,
            market_cap: 0
          })
        });
      } catch (error) {
        console.error('Failed to save current price:', error);
      }

      results.push({
        json: {
          strategy_id: strategy.strategy_id,
          strategy_name: strategy.strategy_name,
          stock_code: stockCode,
          stock_name: stockName,
          current_price: estimatedPrice,
          change_price: changePrice,
          change_rate: changeRate,
          volume: selVolume + buyVolume,
          timestamp: new Date().toISOString()
        }
      });

      console.log(`âœ… ${stockCode} (${stockName}) - ${estimatedPrice.toLocaleString()}ì›`);

    } catch (error) {
      console.error(`âŒ ${stockCode} ì²˜ë¦¬ ì‹¤íŒ¨:`, error.message);
      results.push({
        json: {
          strategy_id: strategy.strategy_id,
          strategy_name: strategy.strategy_name,
          stock_code: stockCode,
          error: error.message,
          timestamp: new Date().toISOString()
        }
      });
    }
  }
}

console.log(`\nâœ… ì „ì²´ ì™„ë£Œ: ${results.length}ê°œ ì¢…ëª© ì²˜ë¦¬`);
return results;
